// Import react
import React from 'react';
// Import react-native components
import {
  SafeAreaView,
  Dimensions,
  StyleSheet,
  View,
  Text,
  Image,
  FlatList,
  Platform,
  StatusBar,
} from 'react-native';
//icon
import { Ionicons } from '@expo/vector-icons';
//Colors
import Colors from '../../../utils/Colors';
//Search Item component
import SearchItem from './SearchItem';
import Animated, { Easing } from 'react-native-reanimated';
import { TouchableOpacity, TextInput } from 'react-native-gesture-handler';
const { Value, timing } = Animated;
// Calculate window size
const { width, height } = Dimensions.get('window');

export class Header extends React.Component {
  constructor(props) {
    super(props);
    // state
    this.state = {
      isFocused: false,
      keyword: '',
      productsFilter: '',
    };
    // animation values
    this._input_box_translate_x = new Value(width);
    this._back_button_opacity = new Value(0);
    this._content_translate_y = new Value(height);
    this._content_opacity = new Value(0);
  }
  //Search
  searchFilterFunction = (searchText) => {
    const data = this.props.products.filter((product) =>
      product.filename.toLowerCase().includes(searchText.toLowerCase()),
    );
    this.setState({ keyword: searchText, productsFilter: data });
  };

  _onFocus = () => {
    // update state
    this.setState({ isFocused: true });
    // animation config
    // input box
    const input_box_translate_x_config = {
      duration: 200,
      toValue: 0,
      easing: Easing.inOut(Easing.ease),
    };
    const back_button_opacity_config = {
      duration: 200,
      toValue: 1,
      easing: Easing.inOut(Easing.ease),
    };
    // content
    const content_translate_y_config = {
      duration: 0,
      toValue: 0,
      easing: Easing.inOut(Easing.ease),
    };
    const content_opacity_config = {
      duration: 200,
      toValue: 1,
      easing: Easing.inOut(Easing.ease),
    };
    // run animation
    timing(this._input_box_translate_x, input_box_translate_x_config).start();
    timing(this._back_button_opacity, back_button_opacity_config).start();
    timing(this._content_translate_y, content_translate_y_config).start();
    timing(this._content_opacity, content_opacity_config).start();
    // force focus
    this.refs.input.focus();
  };
  _onBlur = () => {
    // update state
    this.setState({ isFocused: false });
    // animation config
    // input box
    const input_box_translate_x_config = {
      duration: 50,
      toValue: width,
      easing: Easing.inOut(Easing.ease),
    };
    const back_button_opacity_config = {
      duration: 50,
      toValue: 0,
      easing: Easing.inOut(Easing.ease),
    };

    // content
    const content_translate_y_config = {
      duration: 0,
      toValue: height,
      easing: Easing.inOut(Easing.ease),
    };
    const content_opacity_config = {
      duration: 200,
      toValue: 0,
      easing: Easing.inOut(Easing.ease),
    };
    // run animation
    timing(this._input_box_translate_x, input_box_translate_x_config).start();
    timing(this._back_button_opacity, back_button_opacity_config).start();
    timing(this._content_translate_y, content_translate_y_config).start();
    timing(this._content_opacity, content_opacity_config).start();
    // force blur
    this.refs.input.blur();
  };

  render() {
    const scrollY = this.props.scrollPoint;
    const headerPlatform = 50;
    const clampedScrollY = scrollY.interpolate({
      inputRange: [0, 1],
      outputRange: [0, 1],
      extrapolateLeft: 'clamp',
    });
    const _diff_clamp_scroll_y = Animated.diffClamp(
      clampedScrollY,
      0,
      headerPlatform,
    );
    const _header_translate_y = Animated.interpolate(_diff_clamp_scroll_y, {
      inputRange: [0, headerPlatform],
      outputRange: [0, -headerPlatform],
      extrapolate: 'clamp',
    });
    const _header_opacity = Animated.interpolate(_diff_clamp_scroll_y, {
      inputRange: [0, headerPlatform],
      outputRange: [1, 0],
      extrapolate: 'clamp',
    });
    // const ViewPlatForm = Platform.OS === "android" ? SafeAreaView : View;
    return (
      <>
        <SafeAreaView
          style={{ ...styles.header_safe_area, ...this.props.style }}
        >
          <Animated.View
            style={[
              styles.header,
              {
                transform: [
                  {
                    translateY: _header_translate_y,
                  },
                ],
                opacity: _header_opacity,
              },
            ]}
          >
            <View style={styles.header_inner}>
              <TouchableOpacity
                onPress={() => this.props.navigation.toggleDrawer()}
              >
                <Ionicons
                  name='ios-menu'
                  size={30}
                  color={Colors.red}
                />
              </TouchableOpacity>
              <View>
                <Image
                  source={require('../../../assets/Images/logoNoText.png')}
                  style={{
                    width: height < 668 ? 130 : 120,
                    resizeMode: 'contain',
                  }}
                />
              </View>
              <TouchableOpacity
                activeOpacity={1}
                underlayColor={'#ccd0d5'}
                onPress={this._onFocus}
                style={styles.search_icon_box}
              >
                <Ionicons name='ios-search' size={20} color={Colors.white} />
              </TouchableOpacity>
              <Animated.View
                style={[
                  styles.input_box,
                  { transform: [{ translateX: this._input_box_translate_x }] },
                ]}
              >
                <Animated.View style={{ opacity: this._back_button_opacity }}>
                  <TouchableOpacity
                    activeOpacity={1}
                    underlayColor={'#ccd0d5'}
                    onPress={this._onBlur}
                    style={styles.back_icon_box}
                  >
                    <Ionicons
                      name='ios-arrow-back'
                      size={25}
                      color={Colors.red}
                    />
                  </TouchableOpacity>
                </Animated.View>
                <TextInput
                  ref='input'
                  placeholder='Tìm kiếm sản phẩm'
                  clearButtonMode='always'
                  value={this.state.keyword}
                  onChangeText={(value) => this.searchFilterFunction(value)}
                  style={styles.input}
                />
              </Animated.View>
            </View>
          </Animated.View>
        </SafeAreaView>
        <Animated.View
          style={[
            styles.content,
            {
              opacity: this._content_opacity,
              transform: [{ translateY: this._content_translate_y }],
            },
          ]}
        >
          <View style={styles.content_safe_area}>
            {this.state.keyword === '' ? (
              <View style={styles.image_placeholder_container}>
                <Image
                  source={require('../../../assets/Images/logo1.png')}
                  style={styles.image_placeholder}
                />
                <Text style={styles.image_placeholder_text}>
                  Nhập vào từ khóa{'\n'}
                  để tìm kiếm :D
                </Text>
              </View>
            ) : (
              <View
                style={{
                  marginHorizontal: 20,
                  marginTop:
                    Platform.OS === 'android' ? 0 : height < 668 ? 0 : 60,
                }}
              >
                {this.state.productsFilter.length === 0 ? (
                  <Text style={styles.image_placeholder_text}>
                    Không tìm thấy sản phầm
                  </Text>
                ) : (
                  <FlatList
                    data={this.state.productsFilter}
                    keyExtractor={(item) => item._id}
                    renderItem={({ item }) => {
                      return (
                        <SearchItem
                          item={item}
                          navigation={this.props.navigation}
                        />
                      );
                    }}
                  />
                )}
              </View>
            )}
          </View>
        </Animated.View>
      </>
    );
  }
}

const styles = StyleSheet.create({
  header_safe_area: {
    zIndex: 1000,
    backgroundColor: Colors.white,
    paddingTop: Platform.OS === 'android' ? StatusBar.currentHeight : 0,
  },
  header: {
    position: 'absolute',
    backgroundColor: Colors.white,
    width,
    height: 50,
    top:
      Platform.OS === 'android'
        ? StatusBar.currentHeight
        : height > 736
        ? 40
        : 20,
  },
  header_inner: {
    flex: 1,
    overflow: 'hidden',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 15,
  },
  search_icon_box: {
    width: 35,
